import React, {useState} from 'react';

function Form(props) {
    const [text, setText] = useState('');

    useEffect(() => {
        fetch('/hello')
         .then(res => {
            console.log(res)
            return res.json()
         })
         .then(data => {
        setPlaceholder(data.result);
        });
    }, []);

    // useEffect(() => {
    //     fetchData()
    // }, [])

    const handleSubmit = e => {
      e.preventDefault();
      console.log("making request")
      alert(e.target.value);
    //   fetchData()
    };


    return (
       <form onSubmit={handleSubmit} action="{{ url_for('hello') }}" method="post">
         <label>Enter Text :
           <textarea 
            name= "text"
            value={text}
            onChange={e => setText(e.target.value)}
            rows={5}
            cols={5} 
            placeholder="Enter up to 500 words"
          />
         </label>
         <br />
         <button type="submit" name="Submit" >SUBMIT</button>
       </form>
     );
  }

  export default Form;
