function WordCloud({ phrases }) {
  if (phrases) {
    return (
      <div>
        <TagCloud
          style={{
            fontFamily: "sans-serif",
            fontSize: randomIntFromInterval(),
            fontWeight: "bold",
            fontStyle: "italic",
            color: () => randomColor(),
            padding: 5,
            width: "100%",
            height: "100%",
            marginTop: "40px",
            marginRight: "10px",
            marginLeft: "10px",
            marginBotton: "px",
          }}
        >
          {phrases.map((string) => (
            <div>{string}</div>
          ))}
        </TagCloud>
      </div>
    );
  }
}

export default WordCloud;
