import React, { useState } from "react";
import axios from "axios";
import Button from '@material-ui/core/Button';
import { TextField } from '@material-ui/core';
import DisplayUpper from './DisplayUpper';


function NewForm(props) {
  const [text, setText] = useState("");
  const [fetchedData, setFetchedData] = useState("");
  const [isSubmitted, setSubmitted] = useState(false);


  const handleSubmit = (e) => {
    setSubmitted(true)  // won't work this way

    console.log("button clicked");
    axios
      .post("http://localhost:5000", {
        text: text,
      }.json)
      .then((res) => {
        console.log("res", res);
        setFetchedData(res.text); // whatever the data you want to set in the setFetchedData you can set with res
      })
      .catch((er) => {
        console.log(er);
      });
      setSubmitted(true)  // won't work this way

    }


  if (isSubmitted) {
    return <DisplayUpper prop={fetchedData}/>
  }
  return (
    <div>
    <form onSubmit={handleSubmit} method="post">
      <label>
        Enter Text :
        <TextField multiline={true} variant="outlined"
          name="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
          rows={5}
          cols={20}
          placeholder="Enter up to 500 words"
        />
      </label>
      {/* <br /> */}
      <Button variant="contained" color="secondary" type="submit" name="Submit">
        SUBMIT
      </Button>
    </form>
    {/* {isSubmitted && <DisplayUpper/>} */}
    </div> 
    );
    }


export default NewForm;
