import React, { useState } from "react";
import axios from "axios";
import Button from '@material-ui/core/Button';
import { TextField } from '@material-ui/core';
import Dashboard from './Dashboard';
import LoadingSpinner from './LoadingSpinner';

function NewForm() {
  const [text, setText] = useState("");
  const [fetchedData, setFetchedData] = useState("");
  const [isSubmitted, setSubmitted] = useState(false);

  const [submitDisabled, setSubmitDisabled] = useState(true);
  const [isLoading, setLoading] = useState(true)


  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true) 


    console.log("button clicked");

    const config = {
      headers: {'Access-Control-Allow-Origin': '*'}
  };

    axios
      .post("http://localhost:5000/process", {
        text: text,
      }, config)
      .then((res) => {
        console.log("res", res);
        setFetchedData(res.data); // data you want to set in the setFetchedData is set with res
        setLoading(false);
        console.log(isLoading)
      })
      .catch((er) => {
        console.log(er.response.data);
      });

    }


  // Add a function that will return N characters, wrapped inside JSX, where N is the length of the input in the textarea
  function displayLength() {
    return (
      <div>
        <p>{text.length} characters!</p>
      </div>
    )
  }

  // function validateFieldLength() {
  //   if (submitDisabled && text.length > 100) {
  //     setSubmitDisabled(false);
  //   } else if (!submitDisabled && text.length <= 100) {
  //     setSubmitDisabled(true);
  //   }
  // }

  // // the function called upon onChange
  // const updateFieldLength = e => (
  //   setText(e.target.value), () => (
  //    validateFieldLength()
  //   )
  // )


  if (isSubmitted) {
    {isLoading ? <LoadingSpinner /> :
    <Dashboard pathos={fetchedData.pathos} 
                      logos={fetchedData.logos} 
                      ethos={fetchedData.ethos} 
                      subj_score={fetchedData.subj_score}
                      num_questions={fetchedData.num_questions}
                      phrases_array={fetchedData.phrases}
                      avg_len={fetchedData.avg_len}
                      />
  }
  return (
    <div>
    <form onSubmit={handleSubmit} method="post">
      <label>
      <strong>Input text: </strong><br />
      <small>Must be at least 100 characters.</small><br />
        <TextField multiline={true} variant="outlined"
          name="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
          // onChange={updateFieldLength} OLD NONWORKING WAY
          cols={25}
          rows={10}
          placeholder="Enter text"
        />
      </label>
      <br />
      <Button disabled={text.length <= 3} variant="contained" color="secondary" type="submit" name="Submit">
        SUBMIT
      </Button>
    </form>
    {displayLength()}
    {/* {isSubmitted && <Dashboard/>} */}
    </div> 
    );
    }
  }


export default NewForm;
