import React, { PureComponent } from "react";
import {
  PieChart,
  Pie,
  Sector,
  Cell,
  ResponsiveContainer,
  Legend,
} from "recharts";

// value will need to be percent of uncommon words
const data01 = [{ name: "Uncommon Words", value: { uncommon } }];

let valu_uncommon = data01[0]["value"];
remaining = { name: "Remainder", value: 100 - valu_uncommon };

data01.push(remaining);

// value will need to be percent of repeated words
const data02 = [{ name: "% uncommon repeats", value: { repeats } }];

let valuRepeats = data02[0]["value"];
totalUncommon = { name: "Remainder", value: 100 - valuRepeats };

data02.push(totalUncommon);

const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042"];

function PieChart1({ uncommon, repeats }) {
  // value will need to be percent of uncommon words
  const data01 = [{ name: "Uncommon Words", value: { uncommon } }];

  let valu_uncommon = data01[0]["value"];
  remaining = { name: "Remainder", value: 100 - valu_uncommon };

  data01.push(remaining);

  // value will need to be percent of repeated words
  const data02 = [{ name: "% uncommon repeats", value: { repeats } }];

  let valuRepeats = data02[0]["value"];
  totalUncommon = { name: "Remainder", value: 100 - valuRepeats };

  data02.push(totalUncommon);

  return (
    <PieChart width={800} height={400} onMouseEnter={this.onPieEnter}>
      <Pie
        data={data01}
        dataKey="value"
        cx={180}
        cy={200}
        outerRadius={50}
        fill="#8884d8"
      />
      <Pie
        data={data02}
