import React, { useState } from "react";
import axios from "axios";
import Button from '@material-ui/core/Button';
import { TextField } from '@material-ui/core';
import OverallChart from './OverallChart';


function NewForm(props) {
  const [text, setText] = useState("");
  const [fetchedData, setFetchedData] = useState("");
  const [isSubmitted, setSubmitted] = useState(false);

  const handleSubmit = (e) => {
    // e.preventDefault();
    setSubmitted({isSubmitted: true});
    console.log("making request");
    console.log("making request", fetchedData);
    // alert(e.target.value);
    axios
      .post("http://localhost:8000", {
        text: text,
      })
      .then((res) => {
        console.log("res", res);
        setFetchedData(res.data); // whatever the data you want to set in the setFetchedData you can set with res
      })
      .catch((er) => {
        console.log(er);
      });
  };

  if (isSubmitted) {
    return <OverallChart />
  }
  return (
    <form onSubmit={handleSubmit} method="post">
      <label>
        Enter Text :
        <TextField multiline={true} variant="outlined"
          name="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
          rows={5}
          cols={20}
          placeholder="Enter up to 500 words"
        />
      </label>
      {/* <br /> */}
      <Button variant="contained" color="secondary" type="submit" name="Submit">
        SUBMIT
      </Button>
      {/* <p>{fetchedData}</p> */}
    </form>);
    }


export default NewForm;
