import React, {useState, useEffect} from 'react';

function Form(props) {
    const [text, setText] = useState('');

    useEffect(() => {
    fetch('https://localhost:5000', { 
        method: 'POST', 
        headers: { 'Content-Type': 'application/json', }, 
        body: JSON.stringify({text}), }).then(response => response.json()).then(data => { 
            setText(data.result);
            console.log('Success:', data); }).catch((error) => { console.error('Error:', error); });
},[{text}]);

    // useEffect(() => {
    //     fetch('/').then(res => res.json()).then(data => {
    //     setText(data.result);
    //     });
    // }, []);

    const handleSubmit = e => {
      e.preventDefault();
      console.log("making request")
      alert(e.target.value);
    };


    return (
       <form onSubmit={handleSubmit} action="http://localhost:5000" method="post">
         <label>Enter Text :
           <textarea 
            name= "text"
            value={text}
            onChange={e => setText(e.target.value)}
            rows={5}
            cols={20} 
            placeholder="Enter up to 500 words"
          />
         </label>
         <br />
         <button type="submit" name="Submit" >SUBMIT</button>
       </form>
     );
  }

  export default Form;
