import React from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
} from "recharts";

const MainChart = ({ pathosval, logosval, ethosval }) => {
  const data = [
    { name: "Pathos", value: pathosval },
    { name: "Logos", value: logosval },
    { name: "Ethos", value: ethosval },
  ];

  return (
    <>
      <div>
        <BarChart
          width={500}
          height={300}
          data={data}
          margin={{
            top: 10,
            right: 30,
            left: 10,
            bottom: 5,
          }}
          barSize={20}
        >
          <XAxis
            dataKey="name"
            scale="point"
            padding={{ left: 10, right: 10 }}
          />
          <YAxis />
          <Tooltip />
          {/* <Legend /> */}
          <CartesianGrid strokeDasharray="3 3" />
          <Bar dataKey="value" fill="#8884d8" background={{ fill: "#eee" }} />
        </BarChart>
        {/* <br /> */}
        <p>This is slotting in underneath</p>
      </div>
      <div margin={{left: 20}}>happiness</div>
    </>
  );
};

export default MainChart;
