    return (
       <form onSubmit={handleSubmit}>
         <label>Enter Text:
           <textarea 
            ref={nameEl}
            rows={5}
            cols={5} />
         </label>
         <button type="submit" name="Submit">SUBMIT</button>
       </form>
     );
  }
